const genUsDiv = (start: number, count: number) => {
  const generated = [];
  const pfx = ["North", "South", "East", "West", "Central", "Upper", "Lower", "Grand", "New", "Old", "Fort", "Port", "Lake", "River", "Mount", "Desert", "Coastal", "Valley", "Peak", "Shadow"];
  const sfx = ["City", "Town", "County", "District", "Hills", "Valley", "Plains", "Woods", "Lake", "Shore", "Park", "Heights", "Springs", "Falls", "Grove", "Crossing", "Creek", "Bluff", "Bend", "Ridge"];
  for (let idx = 0; idx < count; idx++) {
    const l = `${pfx[Math.floor(Math.random() * pfx.length)]} ${sfx[Math.floor(Math.random() * sfx.length)]} ${1000 + idx}`;
    const v = `${l.replace(/\s/g, '').substring(0, 3).toUpperCase()}${1000 + idx}`;
    generated.push({ l, v });
  }
  return generated;
};
US_DIVS.push(...genUsDiv(1, 1500));

